package com.lacontraloria.amasuapp.domains;import com.fasterxml.jackson.annotation.JsonInclude;import jakarta.persistence.*;import lombok.*;import java.io.Serializable;import java.time.LocalDateTime;import java.util.Set;@AllArgsConstructor@NoArgsConstructor@Getter@Setter@ToString@EqualsAndHashCode@Entity@Table(name = "VEEDURIA")public class Veeduria implements Serializable {    @Id    @GeneratedValue(strategy = GenerationType.SEQUENCE, generator = "VEESEQ")    @SequenceGenerator(name = "VEESEQ", sequenceName = "VEESEQ", allocationSize = 1)    @Column(name = "IDVEEDURIA", length = 15)    private Long idVeeduria;    @Column(name = "CUI", length = 15)    private Long cui;    @Column(name = "DNIPERSONA", length = 8)    private Long dniResponsable;    @Column(name = "FECHACREACION")    private LocalDateTime fechaCreacion;    @Column(name = "FECHAVEEDURIA")    private LocalDateTime fechaVeeduria;    @ManyToMany    @JoinTable(            name = "VEEDURIA_MONITOR",            joinColumns = @JoinColumn(name = "IDVEEDURIA"),            inverseJoinColumns = @JoinColumn(name = "DNIMONITOR")    )    @JsonInclude(JsonInclude.Include.NON_EMPTY)    private Set<Persona> monitoresAsignados;    @ManyToMany    @JoinTable(            name = "VEEDURIA_MONITOR_POSTULADO",            joinColumns = @JoinColumn(name = "IDVEEDURIA"),            inverseJoinColumns = @JoinColumn(name = "DNIMONITOR")    )    @JsonInclude(JsonInclude.Include.NON_EMPTY)    private Set<Persona> monitoresPostulados;}